INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT030','Cho một tam giác vuông. Khi ta tăng mỗi cạnh góc vuông lên $2cm$ thì diện tích tam giác tăng thêm $17cm^2$. Nếu giảm các cạnh góc vuông đi $3cm$ và $1 cm$ thì diện tích tam giác giảm $11cm^2$. Tính diện tích của tam giác ban đầu?','$50cm^2$','$25cm^2$','$50\\sqrt{5}cm^2$','$50\\sqrt{2}cm^2$','A','phuongtrinh','daicuongpt','3',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT031','Hai vòi nước cùng chảy vào bể thì sau $\\frac{24}{5}$ giờ sẽ đầy bể. Mỗi giờ lượng nước của vòi một chảy được bằng $\\frac{3}{2}$ lần lượng nước của vòi thứ hai. Hỏi vòi thứ hai chảy riêng một mình thì sau bao lâu sẽ đầy bể?','12 giờ','10 giờ','8 giờ','3 giờ','C','phuongtrinh','daicuongpt','3',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT032','Cho phương trình bậc hai $ax^2+bx+c=0$ với $a \\ne 0$. Đặt $\\Delta = b^2 - 4ac$. Khẳng định nào sau đây là đúng?','$a\\left[\\left(x-\\frac{b}{2a}\\right)^2-\\frac{\\Delta}{4a^2}\\right]=0$','$a\\left[\\left(x-\\frac{b}{2a}\\right)^2+\\frac{\\Delta}{4a^2}\\right]=0$','$a\\left[\\left(x+\\frac{b}{2a}\\right)^2-\\frac{\\Delta}{4a^2}\\right]=0$','$a\\left[\\left(x+\\frac{b}{2a}\\right)^2+\\frac{\\Delta}{4a^2}\\right]=0$','C','phuongtrinh','daicuongpt','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT033','Cho phương trình bậc hai $ax^2+bx+c=0$ với $a \\ne 0$. Đặt $\\Delta = b^2 - 4ac$. Khẳng định nào sau đây là đúng?','$ax^2+bx+c=a(x+x_{1})(x+x_{2})=0$','$ax^2+bx+c=a(x-x_{1})(x-x_{2})=0$','$ax^2+bx+c=a(x+x_{1})(x-x_{2})=0$','$ax^2+bx+c=a(x-x_{1})(x+x_{2})=0$','B','phuongtrinh','daicuongpt','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT034','Cho phương trình bậc hai $ax^2+bx+c=0$ với $a \\ne 0$. Hệ thức nào sau đây cho biết phương trình có nghiệm kép:','$a(ax+b)^2=0$','$a(ax-b)^2=0$','$a\\left(a-\\frac{b}{2a}\\right)^2=0$','$a\\left(a+\\frac{b}{2a}\\right)^2=0$','D','phuongtrinh','ptbachai','2',NULL,'10','0');
