INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT036','Cho phương trình $x^2-3x-5=0$. Khi đó tổng các lập phương hai nghiệm phương trình bằng:','40','-40','52','56','D','phuongtrinh','ptbachai','0',NULL,'10','0');
