INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT041','Nghiệm phương trình $(m+3)x^2-3(m-1)x+2m-6=0$. Với $m \\ne -3$ tập nghiệm của phương trình là:','$\\left{1;\\frac{2m-6}{m+3}\\right}$','$\\left{-1;\\frac{2m-6}{m+3}\\right}$','${1;2}$','${-1;-2}$','A','phuongtrinh','ptbachai','1',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT042','Cho phương trình $x^2-(m+2)x+m+1=0$. Với giá trị nào của $m$ để phương trình có hai nghiệm phân biệt và nghiệm này gấp hai lần nghiệm kia:','$m=1$','$m=-\\frac{1}{2}$','$m=1$ hoặc $m=\\frac{1}{2}$','$m=1$ hoặc $m=-\\frac{1}{2}$','D','phuongtrinh','ptbachai','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT043','Cho phương trình $x^2-2(m+1)x+2m+1=0$. Với giá trị nào của $m$ để phương trình có hai nghiệm phân biệt và tổng của hai nghiệm bằng tổng các bình phương của hai nghiệm:','$m=-\\frac{1}{2}$','$m=0$','$m=-\\frac{1}{2}$ hoặc $m=0$','$m=0$ hoặc $m=\\frac{1}{2}$','A','phuongtrinh','ptbachai','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT044','Cặp phương trình nào sau đây tương đương với nhau?','$\\sqrt{x}=\\sqrt{-x}$ và $x=-x$','$x=-\\sqrt{-x}$ và $x^2+x=0$','$|x-2|=2x-1$ và $3x^2-1=0$','$|x|-1=1$ và $x-2=0$','A','phuongtrinh','daicuongpt','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT045','Trong các trường hợp sau, trường hợp nào có thể thay thế dấu "$\\Rightarrow$" bằng dấu "$\\Leftrightarrow$"?','$\\sqrt{2-x}=1 \\Rightarrow x-2=1$','$\\frac{x(x^2-1)}{x^2-1}=-1 \\Rightarrow x=-1$','$x^2=x \\Rightarrow x=1$','$2|1-x|=5 \\Rightarrow 4(1-x)^2=25$','D','phuongtrinh','daicuongpt','3',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT046','Điều kiện xác định của phương trình $\\sqrt{x}=\\sqrt{-x}$ là:','$x \\geq 0$','$x \\leq 0$','$x \\ne 0$','$x=3$','D','phuongtrinh','daicuongpt','0',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT047','Phương trình nào sau đây có điều kiện xác định là $x \\geq 1$?','$x+\\frac{1}{\\sqrt{x-1}}=0$','$x+\\frac{1}{1-x}=\\sqrt{x-1}$','$x+\\frac{1}{x}=\\sqrt{x-1}$','$x+\\frac{1}{x-1}=2x-1$','B','phuongtrinh','daicuongpt','1',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT048','Tập nghiệm của phương trình $(x^2-3x+2)\\sqrt{x-3}=0$ là:','${3}$','${1;2;3}$','${1;2}$','${2;3}$','A','phuongtrinh','ptcan','0',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT049','Phương trình $x-\\sqrt{4-x}=\\sqrt{x-4}+4$:','Có nghiệm $x=4$','Vô nghiệm','Có vô số nghiệm','Nghiệm đúng với mọi $x \\ne 4$','A','phuongtrinh','ptcan','0',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT050','Phương trình nào sau đây có nghiệm?','$\\frac{2x^2-x-3}{\\sqrt{2x-3}}=0$','$\\frac{2x^2-x-3}{\\sqrt{2x-3}}=\\sqrt{2x-3}$','$2|x-2|=2x-5$','$2|x-2|=5-2x$','D','phuongtrinh','daicuongpt','1',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT051','Cho phương trình $m^2x-m=3-x (1), m$ là tham số. Mệnh đề nào sau đây ĐÚNG?','Với mọi giá trị $m$ phương trình $(1)$ luôn có nghiệm duy nhất','Chỉ tồn tại một số giá trị $m$ làm cho $(1)$ có nghiệm duy nhất','Tồn tại giá trị $m$ làm cho $(1)$ vô nghiệm','Tồn tại giá trị $m$ làm cho $(1)$ có vô số nghiệm','A','phuongtrinh','bienluanpt','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT052','Cho phương trình $m^2(x-1)+m=3mx (2)$. Kết luận nào sau đây đúng?','Khi $m=0$, phương trình $(2)$ vô nghiệm','Khi $m=3$, phương trình $(2)$ có vô số nghiệm','Khi $m\\ne3$, phương trình $(2)$ có nghiệm duy nhất $x=\\frac{m-1}{m-3}$','Khi $m\\ne0$, phương trình $(2)$ có nghiệm duy nhất $x=\\frac{m-1}{m-3}$','D','phuongtrinh','bienluanpt','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT053','Mệnh đề "Với mọi $m\\ne0$ thì phương trình có nghiệm duy nhất" KHÔNG ĐÚNG với phương trình:','$\\frac{2}{x-1}=m$','$\\frac{2-x}{x-1}=m$','$m^2(x-1)+m=3m-2$','$m^2x=2-m$','B','phuongtrinh','bienluanpt','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT054','Phương trình $|mx+m-1|=|2x+m|$ tương đương với phương trình:','$|mx+m-1|=2x+m$','$|mx+m-1|=-2x-m$','$mx+m-1=|2x+m|$','$|mx+m-1|=2x+m$ hoặc $|mx+m-1|=-2x-m$','D','phuongtrinh','bienluanpt','1',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT055','Cho phương trình $|mx+m-1|=|2x+m|$. Kết luận nào sau đây KHÔNG ĐÚNG?','Khi $m=1$, phương trình đã cho có nghiệm $x=0$','Khi $m=-3$, phương trình đã cho có nghiệm $x=\\frac{1}{2}$','Khi $m\\ne1$, phương trình đã cho có nghiệm $x=\\frac{1-m}{m+3}$','Khi $m\\ne1$ và $m\\ne-3$, phương trình đã cho có nghiệm $x=\\frac{1+m}{m-1}$ và $x=\\frac{1-m}{m+3}$','C','phuongtrinh','bienluanpt','3',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT056','Phương trình $m^2(x-1)+m=x(3m-2)$ vô nghiệm khi $m$ bằng:','0','1','2','1 và 2','C','phuongtrinh','bienluanpt','0',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT057','Cho phương trình $\\frac{2a}{x+2}=a-2$. Khẳng định nào sau đây là SAI?','Khi $a=2$, phương trình đã cho vô nghiệm','Khi $a\\ne2$, phương trình đã cho có nghiệm duy nhất $x=\\frac{4}{a-2}$','Khi $a=0$, phương trình đã cho vô nghiệm','Khi $a\\ne2$ và $a\\ne0$, phương trình đã cho có nghiệm duy nhất $x=\\frac{4}{a-2}$','B','phuongtrinh','bienluanpt','1',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT058','Cho phương trình $|2mx-m|=|m-x|$. Kết luận nào sau đây KHÔNG ĐÚNG?','Khi $m=-\\frac{1}{2}$, tập nghiệm của phương trình là ${0}$','Khi $m=\\frac{1}{2}$, tập nghiệm của phương trình là $\\left{\\frac{1}{2}\\right}$','Khi $m=\\frac{1}{2}$, tập nghiệm của phương trình là $\\mathbb{R}$','Khi $m \\ne \\pm\\frac{1}{2}$, tập nghiệm của phương trình là $\\left{0;\\frac{2m}{2m+1}\\right}$','B','phuongtrinh','bienluanpt','1',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN10` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT059','Nghiệm của phương trình $-2x+y=-1$ là:','$\\left(\\frac{1+y}{2};2x-1\\right}$ với $x \\in \\mathbb{R}, y \\in \\mathbb{R}$','$x=\\frac{1+y}{2}$ với $y \\in \\mathbb{R}$','$(x;y=2x-1)$ với $x \\in \\mathbb{R}$','$\\left(y;\\frac{1+y}{2}\\right)$ với $y \\in \\mathbb{R}$','C','phuongtrinh','ptbacnhat','0',NULL,'10','0');
