INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT044','Cặp phương trình nào sau đây tương đương với nhau?','$\\sqrt{x}=\\sqrt{-x}$ và $x=-x$','$x=-\\sqrt{-x}$ và $x^2+x=0$','$|x-2|=2x-1$ và $3x^2-1=0$','$|x|-1=1$ và $x-2=0$','A','phuongtrinh','daicuongpt','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT045','Trong các trường hợp sau, trường hợp nào có thể thay thế dấu "$\\Rightarrow$" bằng dấu "$\\Leftrightarrow$"?','$\\sqrt{2-x}=1 \\Rightarrow x-2=1$','$\\frac{x(x^2-1)}{x^2-1}=-1 \\Rightarrow x=-1$','$x^2=x \\Rightarrow x=1$','$2|1-x|=5 \\Rightarrow 4(1-x)^2=25$','D','phuongtrinh','daicuongpt','3',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT046','Điều kiện xác định của phương trình $\\sqrt{x}=\\sqrt{-x}$ là:','$x \\geq 0$','$x \\leq 0$','$x \\ne 0$','$x=3$','D','phuongtrinh','daicuongpt','0',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT047','Phương trình nào sau đây có điều kiện xác định là $x \\geq 1$?','$x+\\frac{1}{\\sqrt{x-1}}=0$','$x+\\frac{1}{1-x}=\\sqrt{x-1}$','$x+\\frac{1}{x}=\\sqrt{x-1}$','$x+\\frac{1}{x-1}=2x-1$','B','phuongtrinh','daicuongpt','1',NULL,'10','0');
