INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT049','Phương trình $x-\\sqrt{4-x}=\\sqrt{x-4}+4$:','Có nghiệm $x=4$','Vô nghiệm','Có vô số nghiệm','Nghiệm đúng với mọi $x \\ne 4$','A','phuongtrinh','ptcan','0',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT050','Phương trình nào sau đây có nghiệm?','$\\frac{2x^2-x-3}{\\sqrt{2x-3}}=0$','$\\frac{2x^2-x-3}{\\sqrt{2x-3}}=\\sqrt{2x-3}$','$2|x-2|=2x-5$','$2|x-2|=5-2x$','D','phuongtrinh','daicuongpt','1',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT051','Cho phương trình $m^2x-m=3-x (1), m$ là tham số. Mệnh đề nào sau đây ĐÚNG?','Với mọi giá trị $m$ phương trình $(1)$ luôn có nghiệm duy nhất','Chỉ tồn tại một số giá trị $m$ làm cho $(1)$ có nghiệm duy nhất','Tồn tại giá trị $m$ làm cho $(1)$ vô nghiệm','Tồn tại giá trị $m$ làm cho $(1)$ có vô số nghiệm','A','phuongtrinh','bienluanpt','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT052','Cho phương trình $m^2(x-1)+m=3mx (2)$. Kết luận nào sau đây đúng?','Khi $m=0$, phương trình $(2)$ vô nghiệm','Khi $m=3$, phương trình $(2)$ có vô số nghiệm','Khi $m\\ne3$, phương trình $(2)$ có nghiệm duy nhất $x=\\frac{m-1}{m-3}$','Khi $m\\ne0$, phương trình $(2)$ có nghiệm duy nhất $x=\\frac{m-1}{m-3}$','D','phuongtrinh','bienluanpt','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT053','Mệnh đề "Với mọi $m\\ne0$ thì phương trình có nghiệm duy nhất" KHÔNG ĐÚNG với phương trình:','$\\frac{2}{x-1}=m$','$\\frac{2-x}{x-1}=m$','$m^2(x-1)+m=3m-2$','$m^2x=2-m$','B','phuongtrinh','bienluanpt','2',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT054','Phương trình $|mx+m-1|=|2x+m|$ tương đương với phương trình:','$|mx+m-1|=2x+m$','$|mx+m-1|=-2x-m$','$mx+m-1=|2x+m|$','$|mx+m-1|=2x+m$ hoặc $|mx+m-1|=-2x-m$','D','phuongtrinh','bienluanpt','1',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT055','Cho phương trình $|mx+m-1|=|2x+m|$. Kết luận nào sau đây KHÔNG ĐÚNG?','Khi $m=1$, phương trình đã cho có nghiệm $x=0$','Khi $m=-3$, phương trình đã cho có nghiệm $x=\\frac{1}{2}$','Khi $m\\ne1$, phương trình đã cho có nghiệm $x=\\frac{1-m}{m+3}$','Khi $m\\ne1$ và $m\\ne-3$, phương trình đã cho có nghiệm $x=\\frac{1+m}{m-1}$ và $x=\\frac{1-m}{m+3}$','C','phuongtrinh','bienluanpt','3',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT056','Phương trình $m^2(x-1)+m=x(3m-2)$ vô nghiệm khi $m$ bằng:','0','1','2','1 và 2','C','phuongtrinh','bienluanpt','0',NULL,'10','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('PT057','Cho phương trình $\\frac{2a}{x+2}=a-2$. Khẳng định nào sau đây là SAI?','Khi $a=2$, phương trình đã cho vô nghiệm','Khi $a\\ne2$, phương trình đã cho có nghiệm duy nhất $x=\\frac{4}{a-2}$','Khi $a=0$, phương trình đã cho vô nghiệm','Khi $a\\ne2$ và $a\\ne0$, phương trình đã cho có nghiệm duy nhất $x=\\frac{4}{a-2}$','B','phuongtrinh','bienluanpt','1',NULL,'10','0');
