INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('DS021','Cho dãy số $(u_n)$ với $\\left\\{\\begin{array} {1}u_1 = 5  \\\\ u_{n+1} = u_n +n  \\end{array} \\right.$Số hạng tổng quát $u_n$ của dãy số là biểu thức nào dưới đây?','$u_n = \\frac{(n-1)n}{2}$','$u_n =5 + \\frac{(n-1)n}{2}$','$u_n =5 + \\frac{n(n+1)}{2}$','$u_n =5 + \\frac{5(n+1)(n+2)}{2}$','B','dayso','dayso','1',NULL,'11','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('DS022','Cho dãy số $(u_n)$ với $\\left\\{\\begin{array} {1}u_1 = 1\\\\ u_{n+1} = u_n +(-1)^{2n}  \\end{array} \\right.$Số hạng tổng quát $u_n$ của dãy số là biểu thức nào dưới đây?','$u_n = 1 +n$','$u_n = 1 - n$','$u_n = 1 + (-1)^{2n}$','$u_n = n$','D','dayso','dayso','2',NULL,'11','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('DS023','Cho dãy số $(u_n)$ với $\\left\\{\\begin{array} {1}u_1 = 1\\\\ u_{n+1} = u_n +(-1)^{2n+1}  \\end{array} \\right.$Số hạng tổng quát $u_n$ của dãy số là biểu thức nào dưới đây?','$u_n = 2 - n$','$u_n$ không xác định ','$u_n = 1 - n$','$u_n = - n$ với mọi n ','A','dayso','dayso','1',NULL,'11','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('DS024','Cho dãy số $(u_n)$ với $\\left\\{\\begin{array} {1}u_1 = 1\\\\ u_{n+1} = u_n + n^2\\end{array} \\right.$Số hạng tổng quát $u_n$ của dãy số là biểu thức nào dưới đây?','$u_n = 1 + \\frac{n(n+1)(2n+1)}{6}$','$u_n = 1 + \\frac{n(n-1)(2n+2)}{6}$','$u_n = 1 + \\frac{n(n-1)(2n-1)}{6}$','Một kết quả khác','C','dayso','dayso','2',NULL,'11','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('DS025','Cho dãy số $(u_n)$ với $\\left\\{\\begin{array} {1}u_1 = 2\\\\ u_{n+1} - u_n = 2n - 1\\end{array} \\right.$Số hạng tổng quát $u_n$ của dãy số là biểu thức nào dưới đây?','$u_n = 2 + (n-1)^2$','$u_n = 2 + n^2$','$u_n = 2 + (n + 1)^2$','Một kết quả khác','D','dayso','dayso','2',NULL,'11','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('DS026','Cho dãy số $(u_n)$ với $\\left\\{\\begin{array} {1}u_1 = \\frac{1}{2} \\\\ u_{n+1} = u_n - 2 \\end{array} \\right.$Khẳng định nào sau đây là đúng ?','Công thức số hạng tổng quát của dãy số này: $u_n = \\frac{1}{2} +2(n-1)$','Công thức số hạng tổng quát của dãy số này: $u_n = \\frac{1}{2} - 2(n-1)$','Công thức số hạng tổng quát của dãy số này: $u_n = \\frac{1}{2} - 2n$','Công thức số hạng tổng quát của dãy số này: $u_n = \\frac{1}{2} + 2n$','B','dayso','dayso','2',NULL,'11','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('DS027','Cho dãy số $(u_n)$ với $\\left\\{\\begin{array} {1}u_1 = -1 \\\\ u_{n+1} = \\frac{u_n}{2} \\end{array} \\right.$Khẳng định nào sau đây là đúng ?','Công thức số hạng tổng quát của dãy số này: $u_n = (-1)\\left(\\frac{1}{2}\\right)^n$','Công thức số hạng tổng quát của dãy số này: $u_n = (-1)\\left(\\frac{1}{2}\\right)^{n+1}$','Công thức số hạng tổng quát của dãy số này: $u_n = \\left(\\frac{1}{2}\\right)^{n - 1}$','Công thức số hạng tổng quát của dãy số này: $u_n = (-1)\\left(\\frac{1}{2}\\right)^{n - 1}$','D','dayso','dayso','2',NULL,'11','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('DS028','Cho dãy số $(u_n)$ với $\\left\\{\\begin{array} {1}u_1 = 2 \\\\ u_{n+1} = 2 u_n \\end{array} \\right.$Khẳng định nào sau đây là đúng ?','Công thức số hạng tổng quát của dãy số này: $u_n = n^{n-1}$','Công thức số hạng tổng quát của dãy số này: $u_n = 2^n$','Công thức số hạng tổng quát của dãy số này: $u_n = 2^{n+1}$','Công thức số hạng tổng quát của dãy số này: $u_n = 2$','B','dayso','dayso','1',NULL,'11','0');
INSERT INTO `danhgiakienthuc`.`NHCHTOAN` (`id`, `noidung`, `dapanA`, `dapanB`, `dapanC`, `dapanD`, `dapan`, `dangtoan`, `dangbt`, `dokho`, `dophancach`, `malop`, `hinh`) VALUES ('DS029','Cho dãy số $(u_n)$ với $\\left\\{\\begin{array} {1}u_1 =\\frac{1}{2} \\\\ u_{n+1} = 2 u_n \\end{array} \\right.$Khẳng định nào sau đây là đúng ?','Công thức số hạng tổng quát của dãy số này: $u_n = -2^{n-1}$','Công thức số hạng tổng quát của dãy số này: $u_n = -\\frac{1}{2^{n-1}}$','Công thức số hạng tổng quát của dãy số này: $u_n = -\\frac{1}{2^n}$','Công thức số hạng tổng quát của dãy số này: $u_n = -2^{n-2}$','D','dayso','dayso ','1',NULL,'11','0');
